//house data
vec2 housePos = vec2(0.2, 0.1);
float houseLengthX = 0.6;
float houseLengthY = 0.4;
vec3 houseColor = vec3(0.9, 0.9, 0.0);
float houseAlpha;
//window1 data
vec2 windowPos = vec2(0.3, 0.25);
vec3 windowColor = vec3(0.2, 0.5, 1.0);
float windowLength = 0.13;
float windowAlpha;
//window2 data
vec2 window2Pos = vec2(0.6, 0.25);
vec3 window2Color = vec3(0.2, 0.5, 1.0);
float window2Length = 0.13;
float window2Alpha;
//chimny data
vec2 chimneyPos = vec2(.3, 0.8);
vec3 chimneyColor = vec3(0.8, 0.4, 0.0);
float chimneyLengthX = 0.05;
float chimneyLengthY = 0.075;
float chinmeyAlpha;
//sunset
float brightness = 1.0;

//switches
float houseSwitch = 1.0;
float windowSwitch = 1.0;
float window2Switch = 1.0;
float chimneySwitch = 1.0;

vec4 BG = vec4(0.0);
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //list to do

    //1. make rectange for house
    //2. make 2 square for windows
    //3. make square for chimny

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float aRatio = iResolution.y / iResolution.x;
    uv.y *= aRatio;

    //----------------------------------------------------------------------------------------------------------------------
    //create house
    vec4 houseLayer = vec4(0.0);
    housePos.y *= aRatio;

    float houseInterpX = step(housePos.x, uv.x) - step(housePos.x + houseLengthX, uv.x);
	float houseInterpY = step(housePos.y, uv.y) - step(housePos.y + houseLengthY, uv.y);
    houseAlpha = (houseInterpX * houseInterpY) * houseSwitch;

    houseLayer = vec4(houseColor, houseAlpha);
    //----------------------------------------------------------------------------------------------------------------------
    //create window1
    vec4 windowLayer = vec4(0.0);
    windowPos.y *= aRatio;

    float window1InterpX = step(windowPos.x, uv.x) - step(windowPos.x + windowLength, uv.x);
	float window1InterpY = step(windowPos.y, uv.y) - step(windowPos.y + windowLength, uv.y);
    windowAlpha = (window1InterpX * window1InterpY) * windowSwitch;

    windowLayer = vec4(windowColor, windowAlpha);
    //----------------------------------------------------------------------------------------------------------------------
    //create window2
    vec4 window2Layer = vec4(0.0);
    window2Pos.y *= aRatio;

    float window2InterpX = step(window2Pos.x, uv.x) - step(window2Pos.x + window2Length, uv.x);
	float window2InterpY = step(window2Pos.y, uv.y) - step(window2Pos.y + window2Length, uv.y);
    window2Alpha = (window2InterpX * window2InterpY) * window2Switch;

    window2Layer = vec4(window2Color, window2Alpha);
    //----------------------------------------------------------------------------------------------------------------------
    //create chinmey
    chimneyPos.y *= aRatio;
    vec4 chinmeyLayer = vec4(0.0);

    float chimneyInterpX = step(chimneyPos.x, uv.x) - step(chimneyPos.x + chimneyLengthX, uv.x);
	float chimneyInterpY = step(chimneyPos.y, uv.y) - step(chimneyPos.y + chimneyLengthY, uv.y);
    chinmeyAlpha= (chimneyInterpX * chimneyInterpY) * chimneySwitch;

	chinmeyLayer = vec4(chimneyColor, chinmeyAlpha);
    //-------------------------------------------------------------------------------------------------------------------
    //create sunset
    vec4 sunSet = vec4(1.0, uv.y, 0.3, 1.0);
   	sunSet.rg *= abs(cos(iTime) * brightness);

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec4 Comp = mix(BG, sunSet, sunSet.a);
    Comp = mix (Comp, houseLayer, houseLayer.a);
    Comp = mix (Comp, windowLayer, windowLayer.a);
    Comp = mix (Comp, window2Layer, window2Layer.a);
    Comp = mix (Comp, chinmeyLayer, chinmeyLayer.a);

   	fragColor = Comp;
}
