void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const int NUMBER_OF_POINTS = 12;

    const vec2[NUMBER_OF_POINTS] points = vec2[](vec2(0.1f,0.1f), vec2(0.2f,0.3f), vec2(0.3f, 0.5f)
                                                 , vec2(0.6f, 0.4f), vec2(0.8f, 1.0f), vec2(0.9f, 0.1f)
                                                 , vec2(1.0f, 0.9f), vec2(0.5f, 0.2f), vec2(0.7f, 0.6f)
                                                 , vec2(0.4f, 0.7f), vec2(0.1f, 0.8f), vec2(0.2f, 0.3f));

    const vec3[NUMBER_OF_POINTS] colours = vec3[](vec3(0.8f, 0.3f, 0.9f), vec3(0.5f, 0.4f, 0.1f)
                                                  , vec3(1.0f, 0.5f, 0.9f), vec3(0.5f, 0.4f, 0.8f)
                                                  , vec3(0.9f, 0.6f, 0.5f), vec3(0.4f, 0.3f, 0.7f)
                                                  , vec3(0.3f, 0.1f, 0.6f), vec3(0.2f, 0.6f, 0.3f)
                                                  , vec3(1.0f, 0.5f, 0.8f), vec3(0.6f, 0.8f, 0.5f)
                                                  , vec3(0.6f, 0.7f, 0.7f), vec3(0.9f, 0.9f, 0.2f));

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // find the closest point from the set
    int nearest = 0;
    for(int i = 0; i < NUMBER_OF_POINTS; i++) {
        if(distance(uv, points[i]) < distance(uv, points[nearest])) {
            nearest = i;
        }
    }

    // Output to screen
    if(distance(uv, points[nearest]) < 0.005f) {
        fragColor = vec4(0, 0, 0, 1.0);
    } else {
    	fragColor = vec4(colours[nearest],1.0);
    }
}
