vec3 hsv2rgb( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
	return c.z * mix( vec3(1.0), rgb, c.y);
}





void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float fft1=texture(iChannel0,vec2(44.0/11025.0,0.25)).r;    
    float fft2=texture(iChannel0,vec2(440.0/11025.0,0.25)).r;    
    float fft3=texture(iChannel0,vec2(4400.0/11025.0,0.25)).r;    

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy*8.0;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*sin(iTime+fft3*16.0+uv.yxx+vec3(0,2,5));
    col += 0.5 + 0.5*cos(iTime*3.0+fft2*4.0+uv.yyy+vec3(0,2,4));
//    col -= abs( sin(iTime+uv.xxx+vec3(0,1,2)) );

//    col = vec3(1)-col;
      col = pow(col,vec3(fft1,2,1));

    // Output to screen
    fragColor = vec4(hsv2rgb(col),1.0);
}
