void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = fragCoord.xy / iResolution.y;
    float aspect = iResolution.x/iResolution.y;
    vec2 mid = uv-vec2(0.5*aspect, 0.5);
    fragColor = vec4(
        (1.0-((length(mid)-0.4-abs(sin(atan(mid.x, mid.y)*2.0+(1.0*sin(iTime))))*-0.4) * 70.0)) * (length(mid)-0.2) * 10.0,
        (1.0-((length(mid)-0.4-abs(sin(atan(mid.x, mid.y)*2.0+(2.0*sin(iTime))))*-0.4) * 70.0)) * (length(mid)-0.2) * 10.0,
        (1.0-((length(mid)-0.4-abs(sin(atan(mid.x, mid.y)*2.0+(3.0*sin(iTime))))*-0.4) * 70.0)) * (length(mid)-0.2) * 10.0,
        1.0);
}
