#define iterations 20.             //between 9 and 42 (with the default constants)
#define r(a) mat2(cos(a),sin(a),-sin(a),cos(a))

const float m=.5;
const float r=2.5;
const float sp1=1.;
const float sp2=.1;
const float scale=1.;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy-.5;
    uv*=scale;
    vec2 c =2.*iMouse.xy/iResolution.xy-1.;
    if(iMouse.xy==vec2(0))
        c=vec2(.2);
    float rythm= texture(iChannel0,vec2(.1,0)).r; 
    //float rythm = texture(iChannel0,vec2(abs(sin((uv.x*uv.y)*.1)),1)).r;
    for(float i=0.;i<iterations;i++)
    {
        uv=abs(uv)-r(iTime*i*sp1+r*rythm/(i+1.))*c;
        uv*=r(iDate.w*sp2)*(m*i+1.);
    }
	fragColor = vec4(sin(.001*uv),.5+.5*sin(.01*length(uv)),1.);
}
