#define I_MAX 50
//#define ROTATION

vec2 	cmult(vec2 a, vec2 b);
vec2	cadd(vec2 a, vec2 b);

void	mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2	uv = fragCoord.xy / iResolution.xy;
    vec4	z = vec4(0.0, 0.0, 0.0, 0.0);
    vec2	of = vec2((uv.x -0.5)/0.125, (uv.y -0.5)/0.125);
    vec3	col = vec3(0.0);
    vec2	dist = vec2(0.0);
    z.xy = of;
#ifdef ROTATION
    vec2	it;
    it = cmult(vec2(1.0, 1.0), vec2(of/(of.x*of.x+of.y*of.y)));
    it /= 3.0;
#endif
    int ii = -1;
    for (int i = -1; i < I_MAX; ++i)
    {
        ++ii;
        z.xy = cadd((cmult(abs(z.xy)-of, abs(z.xy)-of)), vec2(cos(iTime/5.0),sin(iTime/3.0)));
        z.z = 2.0 * (z.x*z.z - z.y*z.w);
        z.w = 2.0 * (z.y*z.z - z.x*z.w);
        dist.x = dot(z.xy,z.xy);
		dist.y = dot(z.zw,z.zw);

#ifdef ROTATION
     z.xy = cmult(z.xy, vec2(cos(it.x+sin(it.y+iTime/2.0)), 
                             sin(it.y+sin(it.x+iTime/2.0))));
#endif
     	col.x += exp(-abs(float(ii)/z.x)); // expensive but pretty
    	col.y += exp(-abs(float(ii)/z.y));
    	col.z += exp(-abs(float(ii)/(z.x+z.y)));
        if (dist.x > 100000000000.0 || dist.y > 100000000000.0)
            break;
    }
    col /= vec3(float(ii));
    fragColor = vec4(col, 1.0);
}

vec2 	cmult(vec2 a, vec2 b)
{
    return (vec2(a.x * b.x - a.y * b.y, a.x * b.y + a.y * b.x));
}

vec2	cadd(vec2 a, vec2 b)
{
	return (vec2(a.x + b.x, a.y + b.y));
}
