void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = 1.0 - fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0, uv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec2 direction = normalize(vec2(sin(iTime * 1.7/4.0) * cos(0.2 + iTime * 0.4/4.0), -6.0));
    vec4 lastColor = 0.1 * (texture(iChannel0, uv + direction * 0.01) +
                              texture(iChannel0, uv + direction * 0.02) + 
                              texture(iChannel0, uv + direction * 0.03) + 
                              texture(iChannel0, uv + direction * 0.04) + 
                              texture(iChannel0, uv + direction * 0.05) + 
                              texture(iChannel0, uv + direction * 0.06) + 
                              texture(iChannel0, uv + direction * 0.07) + 
                              texture(iChannel0, uv + direction * 0.08) + 
                              texture(iChannel0, uv + direction * 0.09) + 
                              texture(iChannel0, uv + direction * 0.10));
    float emissionAmount = .5 + .333 * (sin(uv.y * 6.1 + iTime/4.0 * -1.12) + cos(1.0 + uv.x * 5.7 - iTime/4.0 * 0.7) + sin(1.7 + uv.x * 7.3 + iTime/4.0 * 2.3));
    vec3 newColor = vec3(0., 0.9, 0.05) * emissionAmount * max(0.0, 0.8 - 30.0 * uv.y);
    fragColor = vec4(newColor + .9 * lastColor.rgb * vec3(0.94 + 0.05 * (0.5 * (sin(1.8 + iTime/4.0 * 2.1) +sin(iTime/4.0 * 1.3 + 0.6))), 0.9, 1.0),1.0);
}
