void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float dist;
	float bestDistance = 1000.;
	vec4 color;
	vec4 bestColor;		
    vec2 fc = vec2(fragCoord.x,fragCoord.y);
    vec2 pixel = floor( fc.xy / iResolution.xy * vec2(320,200));
	vec4 samp = texture(iChannel0, pixel / vec2(320,200));

    samp += (texture(iChannel0, pixel / iChannelResolution[1].xy)-0.5)*0.5;
	float mult = iMouse.x / iResolution.x;
	float add = iMouse.y / iResolution.y;
    samp += add;
    samp *= vec4(mult);

    //cga
	check(_,_,_);
	check(o,B,B);
	check(B,o,B);
	check(B,B,B);

	vec4 cga = bestColor;

	fragColor = cga;	
}
