void mainImage(out vec4 fragColor,in vec2 fragCoord)
{

    float blobthres = 0.5 + 0.5 * sin(iTime*.1);
    float phase = iTime*.043;
    vec2 rot = vec2(cos(phase)*fragCoord.x - sin(phase)*fragCoord.y,
        			sin(phase)*fragCoord.x + cos(phase)*fragCoord.y);
    rot *= 1.01 + sin(iTime*.1);
	vec4 tmp = vec4(0.5+0.5*sin(rot.x/10.0+50.0*sin(rot.y/40.0)*sin(iTime*.25)),
                    0.5+0.5*sin(rot.y/10.0+10.0*sin(rot.x/40.0)*sin(iTime*.37)),
                    0,1);
    tmp.b = tmp.r*tmp.g;
    if (tmp.b > blobthres) {
        float plus = 10.0 * (tmp.b - blobthres);
        tmp.r *= 1.0 - plus;
        tmp.g *= 1.0 - plus;
        tmp.b = 0.0;
    }
    fragColor = tmp;
}
