float gridSize = 0.05;
float eps = 0.005;

vec2 complexPow(vec2 c, float d) {
    float a = atan(c.y, c.x) * d;
    return pow(length(c), d) * vec2(cos(a), sin(a));
}

float grid(vec2 p) {
    p *= vec2(1.);
    p += vec2(0.025);
    return min(abs(mod(p.y, gridSize)),
               abs(mod(p.x, gridSize)));
}

vec2 transform(vec2 i, float param) {
    vec2 r = i;
    r.x *= sin(param + i.y) * 0.5 + 1.2;
    r.y *= cos(param + i.x) * 0.5 + 1.2;
    r = complexPow(r, 2.);

    return r;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 res = iResolution.xy;
	vec2 uv = (fragCoord - 0.5 * res) / res.y;


    float col = float(grid(transform(uv, iTime / 5.)) < eps);

	fragColor = vec4(col);
}
