void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    if (uv.y < 0.25) { fragColor = texture(iChannel0, uv); return; }

    float val = texture(iChannel0, uv).r;

    int i = int(val*255.0);
    for (int j=1; j<9; j++) {
        if (float(j)/12.0 < 1.0 - uv.y) i /= 2;
    }

	fragColor = vec4(vec3(0.0),1.0);
    fragColor.r = mod(float(i), 2.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int i = int(255.0 * fragCoord.x / iResolution.x);

    float r = float(i) / 255.0;

    fragColor = vec4(r,0.0,0.0,1.0);
}
