 // --- access to the image of ascii code c from Fabrice 
vec4 char(vec2 pos, float c) {
    pos = clamp(pos,0.,1.);  // would be more efficient to exit if out.
	return texture( iChannel0, pos/16. + fract( floor(vec2(c, 15.999-c/16.)) / 16. ) );
    // possible variants: (but better separated in an upper function) 
    //     - inout pos and include pos.x -= .5 + linefeed mechanism
    //     - flag for bold and italic 
}

float ftc,str=-1.7;


void mainImage( out vec4 O,  vec2 U )
{

    O = vec4(0.);// mac mac cracken ; 
    U /= iResolution.y*2.-1.;
    float t =mod(1.+iTime*1.2,18.0);
    if(t>8.5) ftc=2.2;str=iTime/300.;  
    float tt=str*abs(sin(iTime))*ftc;
    float yy=1.0;



  // text
  U *= 5.; U.x -=-0.3;
  U.y -=0.7+sin(tt);

  O += char(U,min(02.+(t*10.),64.+19.)   ).x; U.x-=.5;
  O += char(U,min(03.+(t*10.),64.+8.)    ).x; U.x-=.5;
  O += char(U,min(05.+(t*10.),64.+1.)    ).x; U.x-=.5;
  O += char(U,min(07.+(t*10.),64.+4.)    ).x; U.x-=.5;
  O += char(U,min(11.+(t*10.),64.+5.)    ).x; U.x-=.5;

  O += char(U,min(13.+(t*10.),64.+18.)    ).x; U.x-=.5;  
  O += char(U,min(17.+(t*10.),64.+20.)    ).x; U.x-=.5;
  O += char(U,min(19.+(t*10.),64.+15.)    ).x; U.x-=.5;
  O += char(U,min(23.+(t*10.),64.+25.)    ).x; U.x-=.5;


 // prime number used to a bit randomize start ascii char :)

    if(t>8.) yy= t-7.; // demo scene 68000 like ; remove -7 to fix yy position ;

   O *= vec4(U.x,sin((U.y*yy)*1.2),1.0,1.0); 

   if(t>8.5){

   U.x+=2.5;   U.y -=-.9; 
   O += char(U,min(29.,64.+25.)    ).x;
   U.x-=.5;    
   O += char(U,min(10.,64.+25.)    ).x;     

   } 
  if(t>18.) t=0.0;
}
