/*
Basic "Pump" action.
Uses basic sin and cos funciton to create a "pump" visual effect
*/

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	/* get coordinate xy */
	vec2 uv = fragCoord.xy / iResolution.xy;

	/* mouse click */
	vec4 m = iMouse;

	/* get cursor coords */
	vec2 cur = vec2(m.x / 2. / iResolution.x, m.y / iResolution.y); 

	/* get frag position */
	vec2 pos = vec2(fragCoord.y / iResolution.y, fragCoord.x / iResolution.x); 

	/* calculate "pump" */
	float value = (cos(pos.y * 3.1415 * 2.0 * 2.0 * cur.y + iTime * 10.0) * cur.x + 1.0) * 0.5;

	/* set pump value */
	float lum = value < pos.y ? uv.y : uv.x;

	/* set color */
	fragColor = vec4(.2, 0.5, lum, 1.0);
}
