#define EPSILON pow(10., -4.)
#define bigness 0.01
#define PI 3.1415926
#define light_col vec3(1., 0., 0.)
#define num_lights 0.
vec3 tex (vec3 p, sampler2D t){
    vec3 t1 = texture(t, p.xy).rgb/3.;
    t1+=texture(t, p.xz).rgb/3.;
    t1+=texture(t, p.yz).rgb/3.;
    return t1;
}
float align (float diff, float dim){
    if(diff>=dim){
        return diff-dim;
    } else {
        return 0.;
    }
}
float box3D (vec3 boxPos, vec3 boxSize, vec3 pos){
    float n = align(abs(pos.x-boxPos.x), boxSize.x)+align(abs(pos.y-boxPos.y), boxSize.y);
    n+=align(abs(pos.z-boxPos.z), boxSize.z);//just to keep the lines short
    return n;
}
float sphere (vec3 spherePos, vec3 pos, float radius){
    return length(pos-spherePos)-radius;
}
//Credit to Inigo Quilez for original function-- https://iquilezles.org/articles/distfunctions
float CappedCylinder( vec3 pos, vec3 p, vec2 h )
{
  p = p-pos;
  vec2 d = abs(vec2(length(p.xz),p.y)) - h;
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}
float cylinder (vec3 pos, vec3 p, float radius, float height){
    vec3 diff = p-pos;
    float yd = abs(diff.y);
    return (length(diff.xz)-radius)+smoothstep(yd, -height*0.5, height*0.6);//+smoothstep(abs(diff.y)-height, 0., -height);
}
float doubleCone (vec3 pos, vec3 p, vec2 s){
    vec3 diff = p-pos;
    float yd = abs(diff.y)-s.y;
    return (length(diff.xz)-s.x)-yd;
}
//useful functions
vec2 rotate2D (vec2 p, float angle){
    float a = atan(p.y, p.x)+angle;
    float m = length(p);
    return vec2(cos(a)*m, sin(a)*m);
}
//from IQ -- https://iquilezles.org/articles/smin
float smin( float a, float b, float k)
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}
float mymin (float a, float b, float k){
    return min(a, b)-abs(a-b)/(k*100.);
}
vec3 calcLight (float t) {
    return vec3(cos(t), sin(t), sin(t*2.))*bigness*2.;
}
vec4 SDF (vec3 p){
    vec3 matCol = vec3(0.);
    float total = 0.;
    float cy1 = CappedCylinder(p, vec3(0.), vec2(1.)*bigness);
    float cy2 = CappedCylinder(vec3(rotate2D(p.xy, iTime*0.5), p.z), vec3(1., 0., 0.)*bigness, vec2(0.1, 5.)*bigness);
    vec3 np = p;
    np.xz = rotate2D(np.xz, iTime*0.7);
    float cy3 = CappedCylinder(np, vec3(1., 0., 0.)*bigness, vec2(0.1, 5.)*bigness);
    np = p;
    np.yz = rotate2D(np.yz, iTime*0.3);
    float cy4 = CappedCylinder(np, vec3(1., 0., 0.)*bigness, vec2(0.1, 5.)*bigness);
    float cut = doubleCone(vec3(0.), p, vec2(1.)*bigness)-0.1*bigness;
    total = max(cy1, -cut);
    total = smin(total, cy2, 0.003);
    total = smin(total, cy3, 0.003);
    total = smin(total, cy4, 0.003);

    return vec4(total, matCol);
}
vec4 trace (vec3 o, vec3 r) {
    float t = 0.;
    vec3 p = o;
    for(int i = 0; i<64; i++){
        p = o+r*t;
        float d = SDF(p).x;
        t+=d*0.7;
    }
    return vec4(p, t);
}
//Credit to Jamie Wong http://jamie-wong.com/2016/07/15/ray-marching-signed-distance-functions/#surface-normals-and-lighting
vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        SDF(vec3(p.x + EPSILON, p.y, p.z)).x - SDF(vec3(p.x - EPSILON, p.y, p.z)).x,
        SDF(vec3(p.x, p.y + EPSILON, p.z)).x - SDF(vec3(p.x, p.y - EPSILON, p.z)).x,
        SDF(vec3(p.x, p.y, p.z  + EPSILON)).x - SDF(vec3(p.x, p.y, p.z - EPSILON)).x
    ));
}

//made this one myself with some tutorials I found out there on the 
float diffuseLight (vec3 n, vec3 r){
    return dot(n, -r);
}
float specularLight (vec3 n, vec3 r){
    vec3 nr = reflect(r, n);
    return pow(max(dot(nr, -r), 0.), 4.);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    //setup coordinates
    vec2 mouse = iMouse.xy/iResolution.xy - vec2(0.5, 0.);
    if(iMouse.xy == vec2(0.))mouse = vec2(0.5, 0.2);
    mouse.x*=5.;
    mouse.y*=2.;
    vec2 uv = fragCoord/iResolution.xy;
	uv-=0.5;
    uv.x*=iResolution.x/iResolution.y;

    //setup camera + rays
    float angle = mouse.x;
    float fov = 0.5;
    float camDist = bigness*6.;
    vec3 camera = vec3(cos(mouse.x)*camDist, 0., sin(mouse.x)*camDist);
    vec3 ray = vec3(-cos(mouse.x+uv.x*fov)*2., uv.y, -sin(mouse.x+uv.x*fov)*2.);
    ray = normalize(ray);
    mouse.y-=0.5;
    mouse.y*=-2.5;
    camera = vec3(rotate2D(camera.xy, mouse.y), camera.z);
    ray = vec3(rotate2D(ray.xy, mouse.y), ray.z);

    //do the raymarching
    vec4 v = trace(camera, ray);
    vec3 normal = estimateNormal(v.xyz);//keep here
    vec3 col = vec3(0.);
    col = SDF(v.xyz).yzw;//material color     
    //do the lighting https://learnopengl.com/Lighting/Basic-Lighting
    vec3 ligr = -normalize(v.xyz-calcLight(iTime*0.5));
    col+=texture(iChannel0, reflect(ray, normal)).rgb;//reflection
    if(length(col)<=0.){
    	col+=diffuseLight(normal, ray)*0.5;
    }

    float fog = 1./(1.+pow(v.w*2., 10.)*0.1);//distance shading
    col = col*fog + (1.-fog)*texture(iChannel0, ray).rgb;
    // Output to screen
    fragColor = vec4(col,1.0);
}

//the drawing buffer
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 r = iResolution.xy;
    vec2 uv = fragCoord/r.xy;
    vec4 col = vec4(0.);
    vec2 mouse = iMouse.xy/r.xy;
    col = max(texture(iChannel0, uv-vec2(0.001, 0.01))-0.01-texture(iChannel1, uv).r*0.1, 0.);
    ivec2 f = ivec2(fragCoord);
    float focus = 4.;
    if (iResolution.x>1000.){
        focus = 2.;
    }
    float blur = (
        col*focus+
        texelFetch(iChannel0, ivec2(f.x, f.y+1), 0)+
        texelFetch(iChannel0, ivec2(f.x+1, f.y), 0)+
        texelFetch(iChannel0, ivec2(f.x-1, f.y), 0)+
        texelFetch(iChannel0, ivec2(f.x, f.y-1), 0)
    ).r/(4.+focus);
    col = vec4(blur);
    if (fragCoord.y<1.){
        col = vec4(1.01);
    }
    fragColor = col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float b = 0.;
    for(float i = 0.; i<3.; i++){
        b+=texture(iChannel0, uv*pow(2., i)-vec2(0., iTime*0.2)).r*pow(0.8, i);
    }
    fragColor = vec4(b*b*b)*0.2;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = vec3(0.);
    col = texture(iChannel0, uv).rgb;
    if (col.r>1.0){
        col = vec3(100., 100., 100.);
    } else {
        col.g*=col.g;
        col.b = 0.;
    }
    // Output to screen
    fragColor = vec4(col,1.0);
}

#define PI 3.14159
void mainCubemap( out vec4 fragColor, in vec2 fragCoord, in vec3 rayOri, in vec3 rayDir )
{
    // Ray direction as color
    float angle = atan(rayDir.z, rayDir.x);
    vec3 col = texture(iChannel0, vec2(angle/PI/2., 
                                       max(abs(rayDir.y*1.), 0.004)
                )).rgb;
    // Output to cubemap
    fragColor = vec4(col,1.0);
}
