void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float mid = (iResolution.x / iResolution.y) * .5;
//	uv.x *= iResolution.x / iResolution.y;
	uv = uv * 2. - 1.;
    uv*=.5;
    uv = abs(uv*.05);
    uv.x *= iResolution.x / iResolution.y;
//    uv.x -= .3;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx*2.+vec3(0,2,4));
	float d = distance(uv, vec2(mid, .5));

	float b = smoothstep(0.5, .6, d);
    float s = 1. - max(d, b);
    float sm = pow(abs(fract(1.*d+iTime*.1))+.9, 10.);
    float f = smoothstep(.6, .99, s);
    // Output to screen
    fragColor = vec4(vec3(max(1., fract(sm)*5.) * .1 * col),1.0);
}
