//inspiried by book of shaders and patriciogv

float plot(vec2 st, float pct){
  return  smoothstep( pct-0.1, pct, st.y) -
          smoothstep( pct, pct+0.1, st.y);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 st = fragCoord.xy/iResolution.yy;
    //st.x = st.x * iResolution.x/iResolution.y;
    st = st*.5;
    st.y = st.y + .25;
    //st.x = st.x - .25*(iResolution.x/iResolution.y);
    vec3 color = vec3(0.0);

    vec2 pos = vec2(0.5)-st;
	vec2 pos2 = vec2(0.5)-st;
    float r = length(pos)*2.0;
    float a = atan(pos.y,pos.x);
    float b = atan(pos2.y,pos2.x);

    float f = cos(a*3.);
     f = abs(cos(a*3.));
     f = abs(cos(a*2.5+sin(iTime/2.0)*8.0))*.5+.3;

	f = plot(st,f);
    color = vec3( 1.-smoothstep(f,f+0.02,r) );
	vec3 col = 0.5 + 0.5*cos(iTime+st.xyx+vec3(0,2,4));
    fragColor = vec4(col*color, 1.0);
}
