void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float fft=texture(iChannel0,vec2(440.0/11025.0,0.25)).r;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy * 1.0;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*tan((iTime*2.2)+uv.xyx+vec3(0,2,1));
    col += 0.5 + 0.5*tan(iTime+uv.yxy+vec3(0,5,16));
    col -= abs(tan(iTime*1.1+uv.xyy+vec3(3,5,3)));
    col += abs(tan(iMouse.x*6.0+uv.xyy+vec3(3,5,3)));
    col = pow(col,vec3(6));

    // Output to screen
    fragColor = vec4(col,1.0);
}
