//Jamie Pendergast
vec2 c_mul(vec2 l, vec2 r)
{
    vec2 z;
    z.x = (l.x * r.x) - (l.y * l.y);
    z.y = (l.x * l.y) + (r.x * r.y);
    return z;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 c = -1. + 2. * fragCoord/iResolution.xy;
    c.x *= iResolution.x / iResolution.y;
    float sc = exp(-float(iFrame) * 0.01);
    c *= sc;
    vec2 z = c;
    int i = 0;

    for(i; i < 20; ++i)
    {
        if(dot(z,z) > 4.) break;
        z = reflect(z + z,c_mul(z,z));
    }
    fragColor = vec4(3.3 * (float(i) - log2(log2(dot(z,z) - 1.)) + 4.) / 100.);
}
