vec3 col(vec2 cord, float Time) {
    float a = .5+.5*sin(cord.x+Time);
    float ax = mod(cord.x-Time*5., 10.)/10.;
    float ay = mod(cord.x+cord.y, 15.)/15.;
    a = mod(ax+ay, 2.)/2.;
    a = pow(a, 1.2);
    float b = .5+.5*sin(cord.x+10.*sin(cord.y/4.-Time)+Time*2.);
    return vec3(min(a, b), a, max(a, b));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 cord = 100.*fragCoord/iResolution.x;

    vec3 color = col(cord, iTime);



    fragColor = vec4(color, 1.);


}

// current code
vec2 mainSound2( float time ) {
    // some credit to https://pages.mtu.edu/~suits/notefreqs.html for note frequencies
    float C5 = 261.63*2.;
    float E5 = 329.63*2.;
    float B4 = 246.94*2.;
    float G3 = 196.00*2.*2.;
    float pitch = 100.;
    if (mod(time, .5) > .25) {
        pitch = E5;
    }
    else {
        if (mod(time, 1.) > .5) {
            pitch = G3;
        }
        else {
            if (mod(time, 4.) < 2.) {
            	pitch = C5;
            }
            else {
                pitch = B4;
            }
        }
    }
    // return vec2( sin(6.2831*pitch*mod(time, 4.0)) );
    return vec2(abs(sin(6.3 * pitch * mod(time, 1.25) / 3. + sin(time * 25.))), sin(6.289 * pitch * mod(time, 1.12) - .4)) - .1;
}

vec2 mainSound( in int samp, float time ) {
    return (mainSound2(time-.25)+mainSound2(time))*mod(time+100., 0.5);
}






