void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
	vec2 px = 1./iResolution.xy;

    vec4 v = texture(iChannel0, uv);

    vec4 r = texture(iChannel0, uv+px*vec2(1,0));
    vec4 tr = texture(iChannel0, uv+px);
    vec4 t = texture(iChannel0, uv+px*vec2(0,1));


    vec4 b = texture(iChannel0, uv-px*vec2(1,0));
    vec4 bl = texture(iChannel0, uv-px);
    vec4 l = texture(iChannel0, uv-px*vec2(0,1));

    vec4 br = texture(iChannel0, uv+px*vec2(1,-1));
    vec4 tl = texture(iChannel0, uv-px*vec2(1,-1));




    fragColor = v/3.+((r+l+b+t)/4.)/3.+((tl+tr+bl+br)/4.)/3.;
}

float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

void mainImage( out vec4 c, in vec2 f )
{
    vec2 uv = f/iResolution.xy*2.-1.;
    uv.x*=iResolution.x/iResolution.y;
    uv*=1.3;

    vec2 ov = uv;

    float r=atan(uv.x,uv.y) - 0.2;// + 0.025*sin(iTime);
    float l=length(uv);

    uv=vec2(sin(r),cos(r))*l;

    uv.y+=sin(uv.x*acos(-1.))/10.;
    vec3 col = vec3(0);

    ov+=iResolution.x/iResolution.y*(vec2(rand(vec2(iTime,iDate.w)),rand(vec2(iDate.w,iTime)))*2.-1.);

    if ((length(ov)<.1&&length(ov-vec2(.1,0))>.08||abs(ov.x)<sin(ov.y*40.+2.3)/50.&&ov.y>0.&&ov.y<.2)&&iTime>2.)
    {
    	col=vec3(.6,.6,.6);
    }

	if (max(uv,vec2(-1,-1))==uv&&min(uv,vec2(1,1))==uv)
    {
        if (abs(uv.x)>.03&&abs(uv.y)>.03) {
            if (uv.x>0.)
            {
                if (uv.y<0.)
                {
                    col=vec3(242,123,0);
                }
                else
                {
                    col=vec3(41,98,26);
                }
            }
            else
            {
                if (uv.y<0.)
                {
                    col=vec3(51,89,155);
                }
                else
                {
                    col=vec3(190,8,2);
                }
            }
            col/=255.;
            col*=1.+(1.3-length(uv))/1.5;
            col+=(1.3-length(uv))/2.;
        }
    }



    c = vec4(col*mix(0.,1.,-cos(clamp(iTime,0.,1.)*acos(-1.))/2.+.5),1.0);
}
