#define SS 4
vec3 subsample(vec2 fc){
    vec2 res= floor(iResolution.xy);
    vec2 uv = fc/res;
    vec2 s= uv*2.-1.;
    s.x*= res.x/res.y;
    uv= s;
    uv*= 1.0001;
    int a= 0;
    for(int i=1; i<= 32; i++){
        float c= pow(2.+cos(iTime*.5),float(i));
        vec2 v= mod(abs(uv)*c, vec2(1))*2.-1.;
        a+= max(v.x,v.y)>.0? 1:0;
        //if(a<.0)
            //b= true;
    }

    vec3 c;
    a=a/2-6;

    switch(a){
    case 0:
        c= vec3(0.,0.,0.);
        break;
    case 1:
        c= vec3(1.,0.,0.);
        break;
    case 2:
        c= vec3(0.,1.,0.);
        break;
    case 3:
        c= vec3(0.,0.,1.);
        break;
    case 4:
        c= vec3(0.,0.,1.);
        break;
    case 5:
        c= vec3(0.,1.,1.);
        break;
    case 6:
        c= vec3(.5,0.,1.);
        break;
    case 7:
        c= vec3(1.,.5,0.);
        break;
    };

    //float l= float(a)/32.;
    //c= vec3(l);
    return c;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 fc = floor(fragCoord);

    vec3 c= vec3(0);
    for(int y=0; y!=SS; y++){
    	for(int x=0; x!=SS; x++){
            c+= subsample(fc + vec2(x,y)/float(SS));
        }
    }
    c/=float(SS*SS);
    //c= pow(c,vec3(1./2.4));//srgb
    //c+= rand(length(fc)+iTime)/250.;//dither

    fragColor= vec4(c,1);
}
