#define pi 3.14159265359

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 col = (fragCoord - 0.5 *iResolution.xy) / iResolution.y;
    float radial = (atan(col.x,col.y) / pi) + (iTime * 0.5);

    float glow_cent = 1.0 - pow(length(col), 2.0);
    float glow_anim = pow((glow_cent * 1.0), 5.0) + (glow_cent + max((sin(iTime * 14.935314) + 2.0) / 
                                                  2.0,(sin(iTime * 19.35636) + 2.0) / 2.0));

    float circle = pow(length(col), 2.0) * iTime/100.0 +(iTime);

    vec3 color_grad = mix(vec3(1.0,0.6,0.5), vec3(.5,0.8,1.0), pow(glow_cent, 5.0));

    float circle_mov = length(col) - iTime;

    col = vec2(radial,circle_mov);

    vec4 tex = (texture(iChannel0, col, 1.0) * 1.0) + (glow_anim * 0.3) * vec4(color_grad,1.0);


    fragColor = vec4(tex);

}


