void mainImage( out vec4 fragColor, in vec2 fragCoord ) { 
    vec2 uv = fragCoord/iResolution.xy;

    vec2  vector = uv * 2.0 - 1.0;

    vector.x *= (iResolution.x/iResolution.y);

	float radius = length(vector);

    float angle = atan(vector.y/vector.x);

    float radius1 = sin(iTime * 2.0) + sin(40.0*angle+iTime)*0.01;
    float radius2 = cos(iTime * 3.0);

    vec3 color1 = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    float inten1 = 1.0 - sqrt(abs(radius1 - radius));
    float inten2 = 1.0 - sqrt(abs(radius2 - radius));

    fragColor = vec4(color1 * (inten1 + inten2), 1.0);
}
