void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    uv -= 0.5;

    float angle = atan(uv.y, uv.x); //-pi et pi
    float radius = length(uv);

    angle /= 3.1416;
    angle *= 4.;    

    float osc = cos(iTime)*0.25 + 0.5;
    osc = log(osc)+1.;

    float should = mod(angle, 1.);

    uv = vec2(angle+osc, radius * 4.);

    uv.x = mix(uv.x + radius, uv.x - radius, should);
    uv.y -= osc*2.;

    vec4 col = texture(iChannel0,uv);

    float lum = (col.x + col.y + col.z)/3.0;

    vec4 colA = vec4(0.0, 0.0, 1.0, 1.0);
    vec4 colB = vec4(1.0, 1.0, 0.0 , 1.0);

    col = mix(colA, colB, osc);
    col *= lum;

    fragColor = col;
}
