#define rot2(spin) mat2(sin(spin),cos(spin),-cos(spin),sin(spin))

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.y*2.0-iResolution.xy/iResolution.y;

    bool l = false;
    mat2 rot = rot2(iTime * 0.1);

    for(int i = 0; i < 64; i++) {

        uv *= 1.1 * rot;

        if(uv.y > 1.0) {
            break;
        }
        l = !l;
    }

	fragColor = vec4(l);
}

