       */


void mainImage(out vec4 o,vec2 i )
{  
    vec2 r=iResolution.xy,
        u=(i+i-r)/r.y,
        v=vec2(0,.25);

    //movement
   u*=2.+cos(iDate.w);
    //  

    //pattern
    //u.x+=floor(u.y)/2.;
    u=fract(u)-.5;
    //

    o=.5-vec4(length(u)<.5)*sign(u.x);	

    for (float x=12.;x>-13.;x-=8.)
      o-=x*vec4(length(u+(v=-v))<1./abs(x)); 

}

