void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 st = fragCoord.xy / iResolution.xy;
	float size = .12;
	vec2 num = vec2(ivec2( size * iResolution.xy ));

	vec2 uv = floor(st*num);

	fragColor = vec4( mix( vec3(1.00, 1.00, 1.00), 
                       vec3(0.82, 0.84, 0.87), 
                        mod(uv.x+uv.y,2.0) ), 1.0 );
}
