const float E = 2.718281828459045;
const float expected = 1.648721270700128; // e ^ 0.5

float compare( float value ) {
  return abs( value - expected );   
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

   	// Test exp function (bugged in some Android implementations)
    // Compare the result of a number ways of calculating exponents
    float difference = 0.0;
    if ( uv.y < 0.25 ) {
        difference = compare( exp( 0.5 ) );
    } else if ( uv.y < 0.5 ) {
        difference = compare( pow( E, 0.5 ) );
    } else if ( uv.y < 0.75 ) {
        difference = compare( sqrt( E ) );
    } else {
        difference = compare( expected );
    }

    // Highlight error by messing up image
    vec4 color = texture( iChannel0, uv );
    color.rgb += vec3( 0.01 * difference, difference, 100.0 * difference );
    fragColor = color;
}
