#define r  iResolution.xy
#define m iMouse
vec2 h(vec2 i){
    return fract(vec2(sin(i.y*845.589+iDate.w)*5865.9,sin(iDate.w+i.x*8454.589)*865.9));}
void mainImage( out vec4 O,vec2 i )
{
    vec2 I =i+h(i);//montecarlo anti aliasing
    vec2 c = m.x>0.?2.*(2.*m.xy - r)/r.y:vec2(-.8,.2);
    O =vec4(0.,0,9,0);
    I = 2.*(2.*I - r)/r.y;
    if( m.z>0.&&max(I.x,I.y)<.5) //comment this line to see the antialiased "kalibrot" set
        c=I;
    for(int i =0;i<99;i++)
    {
        I=abs(I)/dot(I,I)+c; //I=1/abs(I)+c (abs(I)!=length(I))
        O= vec4(max(O.x,length(I)),max(abs(I.x),O.y),min(abs(I.y),O.z),0);
    }

    O=pow(clamp(cos(vec4(cos(O.x),O.y,log(O.z),0)),0.,1.),vec4(2.24));
    vec4 b=texture(iChannel0,i/r);
    O = mix(b,O,b.a==1.?.01:1.);
    O.a=float(m.z<0.||m.x==0.);
}
