void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Setting the base wood texture
    // combines image and light wood color
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 woodColor = texture(iChannel0, uv);

    vec2 uvscaled = vec2(uv.x/3.0, uv.y);
    vec4 worlColor = texture(iChannel1, uvscaled);
    vec4 lightBrown = vec4(1.0, 0.78, 0.52, 1.0);

    // Get magnitude of worlTexture
    float worlMag = clamp(1.0-(worlColor.r + worlColor.g + worlColor.b)/3.0, 0.7, 0.8)+0.2;



    vec4 baseWoodColor = mix(lightBrown, woodColor, 0.2);

    baseWoodColor = mix(woodColor, baseWoodColor, worlMag);


    fragColor = baseWoodColor;
}
