//Based on this: https://www.shadertoy.com/view/XstXzs (135 chars ;-p ) posted by FabriceNeyret2

void mainImage( out vec4 O, vec2 U )
{
    O-=O;
	U = 2.2 * U/iResolution.y - 1.1; 
    U.x =asin(U.x/cos(U=asin(U)).y);O.g=.1*U.x;
    O+=2.*(O.r=.2*U.x)+texture(iChannel0,.5*(U+iDate.wx)); 
    O*=O*.8;
}
