#define PARTICLE_COUNT 160
#define PARTICLE_SIZE 0.001

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 outColor = vec3(0);
    float radius = 0.01;

	vec2 uv = fragCoord.xy / iResolution.xy;
	uv = 2.0 * uv - 1.0;
    uv.x -= 0.85;
    uv.x *= iResolution.x/iResolution.y;

    for(int i=0; i<PARTICLE_COUNT; i++)
    {
		// Create more circles
        vec2 space = uv;
        space.x += PARTICLE_SIZE*20.0*float(i);
        space.y += sin(0.02*iTime*float(i));
        float sDot = dot(space, space);
        if(sDot < PARTICLE_SIZE)
        {
            outColor = vec3(uv.y, 1.4 * fragCoord.x / iResolution.x, 0.0);
        }
        else
        {
            outColor += vec3(0.001);
        }
    }

    fragColor = vec4(outColor, 1.0);
}
