vec3 v3( float x, float y, float z ){
	return vec3( x, y, z );
}

vec3 h2rgb( float hue ){

  float h = abs(hue - floor(hue)) * 6.;
  vec3 c = vec3( 0., 0., 0. );

  int f = int(floor( h ));

  if(f==0)c=v3(1.,h,0.);else if(f==1)c=v3(2.-h,1.,0.);else if(f==2)c=v3(0.,1.,h-2.);else if(f==3)c=v3(0.,4.-h,1.);else if(f==4)c=v3(h-4.,0.,1.);else c=v3(1.,0.,6.-h);
  return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 q = fragCoord.xy - iResolution.xy / 2.;

    vec3 originColor = h2rgb( iTime / 10. );

    float dist = sqrt(q.x*q.x + q.y*q.y);
    float radius = 100.;

	fragColor = vec4( dist < radius ? originColor : originColor / sqrt( dist - radius + 1. ), 1 ) ;
}
