vec2 wave(vec2 uv, float amplitude, float frequency, float speed)
{
    vec2 newUV = vec2(uv.x, uv.y + sin((uv.x * frequency) + mod(iTime, frequency) * speed) * amplitude);
    return newUV;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
