float scale = 10.0;

float board(vec2 uv)
{
	uv = floor(uv * 2.0);

    return mod(uv.x + uv.y, 2.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy * scale;
    vec3 color = vec3(board(uv));

    fragColor = vec4(color, 1.0);
}
