{
    O = texture(iChannel0, U / iResolution.xy);

     O = smoothstep(.02,.0,  mix (  -(O.y-.12-.09*sin(6.28*O.x+iDate.w)),
                                  abs(O.y-.12-.09*sin(6.28*O.x+iDate.w)), 
