#define T(i,j) texture(iChannel0, fract( ( U +vec2(i,j) ) / iResolution.xy) )

void mainImage( out vec4 O,  vec2 U )
{
    O = T(0,0);

    float c=0.,v ;
    for (int j=-1; j<2; j++)
        for (int i=-1; i<2; i++) {
            v = T(i,j).x;
            if (v!=0.) if (c==0.) c = v;       // 1st influence in area
                      else if (c!=v) return;   // more than one influence here
        }

	O.x = c;

    if (O.y < .9)
	O.y = ( T(-1, 1) + T(0, 1) + T(1, 1) 
          + T(-1, 0) + T(0, 0) + T(1, 0) 
          + T(-1,-1) + T(0,-1) + T(1,-1) ).y / 9.*1.; 

    U -= 9.*iTime;
    if (O.x ==0. && rnd(U)>.9991) O = vec4(rnd(U+.1),1,1,0);
  //if (O.x ==0. && rnd(U+.2)>.9992) O = vec4(-rnd(U+.3),1,1,0);
}
