#define rnd(U) fract(45678.*sin(dot(U,vec2(1,17.43))))

void mainImage( out vec4 O,  vec2 U )
{
    vec2 R = iResolution.xy;
    //U = 1.2*U - .1*R;
    //vec2 V = abs(U/R-.5);
    O = // max(V.x,V.y)> .5 ? O-O : 
   		.5+.5*texture(iChannel0,U/R);

	U *= 10./R.y;
	U += .07*sin ( 3.14*U.yx);
    float rx = rnd(floor(U+vec2(.5,0))) -.5,
          ry = rnd(floor(U+vec2(0,.5))) -.5,
          r = .2;
    U = fract(U)-.5;
    if (abs(U.x)<min(r,abs(U.y))) U.y += sign(ry)*sqrt(r*r-U.x*U.x);
    if (abs(U.y)<min(r,abs(U.x))) U.x += sign(rx)*sqrt(r*r-U.y*U.y);
    U = fract(U+.5)-.5;
    U = smoothstep(0.,20./R.y,.5-abs(U));
    O *= min(U.x,U.y);
}
