void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy;
	fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);


    float minDist = length(uv - iMouse.xy);
    fragColor = vec4(1,1,0,1);

    for(float i=0.0;i<=5.0;i++) {
        for(float j=0.0;j<=5.0;j++) {
            vec2 normalSpot = texture(iChannel0,vec2(j/5.0,i/5.0)).xy;
            float newDist = length(uv - normalSpot*iResolution.xy);
            if(newDist < minDist) {
                minDist = newDist;
                fragColor = vec4(i/5.0,j/5.0,1,1);
            }
        }
    }

    if(minDist < 3.0) {
        fragColor = vec4(0,0,0,1);
    }
}
