#define TAU 6.2831853071
#define PI 3.14159265359

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 r = iResolution.xy;
	vec2 uv = 0.5 + (fragCoord -.5*r)/r.y;

    float particlePower = 0.0;

    vec2 p = vec2(0.5)-uv;

    float angle = (atan(p.x,p.y)/TAU)+0.5;
    float dc = length(vec2(0.5)-uv);
    fragColor = vec4(1.0);
    float rand = texture(iChannel0,vec2(angle,iTime*0.02)).x;
    float shinePower = pow((sin(iTime*1.0)+2.0)*1.2,1.5);
    rand = clamp(rand*dc*8.0,0.5,0.8);
    fragColor = fragColor-vec4(0.0,rand*0.2*dc*shinePower,rand*dc*shinePower,0.0);
}
