#define v3 vec3

vec3 h2rgb( float hue ){

  float h = abs(hue - floor(hue)) * 6.;
  vec3 c = vec3( 0., 0., 0. );

  int f = int(floor( h ));

  if(f==0)c=v3(1.,h,0.);else if(f==1)c=v3(2.-h,1.,0.);else if(f==2)c=v3(0.,1.,h-2.);else if(f==3)c=v3(0.,4.-h,1.);else if(f==4)c=v3(h-4.,0.,1.);else c=v3(1.,0.,6.-h);
  return c;
}
float mod2( float x, float y ){
	return fract(x*y)*y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 q = fragCoord.xy;

    float light = mod2( iTime / 5000. + sin( q.y*q.y*q.y / 1e10 ) + cos( q.x * 3. ), iResolution.y );
	fragColor = vec4( h2rgb( q.x / iResolution.x ) / light, 1 );
}
