#define repeat(v, r) (mod(v, r)-r/2.)

struct Shape{
  float dist;
  vec4 color;
};



//=======================
// Utility Functions
//=======================
float random(vec2 v) {
  return fract((dot(v*0.1, vec2(324.654, 156.546)))*46556.2*2.);
}

mat2 rot(float a){
  float r = sin(a)*cos(a); //Makes specific pattern of ripples
  float f = sin(a)*sin(a); //Smooths out background effects
  return mat2(r, f, -f, r);
}


float pole(vec2 v, float r){
  return length(v)-r;
}
/*float sphere(vec3 v, float r){
  return length(v)-r;
}
float box(vec3 v, float r, float m) {
  return max(length(v.xy)-r, abs(v.z)-m);
}
*/


float mixColors(float r, float v, float z){
  return clamp(0.5+0.5*(v-r)/z, 0., 1.);
}

float mixShapes(float v, float f, float r){
  float z = mixColors(v, f, r);
  return mix(f,v,z)-r*(2.-z);
}

float pModPolar(inout vec2 v, float r){
  float f = 6.28318/r;
  float z = atan(v.y, v.x)+f*0.5;
  float m = floor(z/f);
  z = mod(z, f)-f*0.5;
  v = vec2(cos(z), sin(z))*length(v);
  return m;
}
//=======================

Shape vines(vec3 c){
  Shape shape;
  float vine;
  shape.dist = 10.; // Draw Distance
  shape.color = vec4(1.); // Initial Color

  // Shape Attributes
  float twist = 10.; // Ripple Repeat
  float vineOffset = 5.; // Ripple Offset


  // Stems
  vec3 i = c;
    i.xy *= rot(c.y*0.1+iTime*0.2); //Bends the Water Ripples
    i.xz *= rot(c.y*.02); //Adds to Bending Effect
    float detail = pModPolar(i.xz, twist); //Flattens the vines into smooth pattern
    float x = mix(0., abs(sin(iTime)*.5)*.5, mod(detail*2., 20.)); // Small Ripples
    i.x -= vineOffset* 4.; // Offset Ripples
  	float stemWidth = 0.3; //Gradually defines ripples more
  	vine = pole(i.xz, stemWidth+0.02*sin(i.y*x)); //Smooth out the ripples


  vec4 color = vec4(0.5, .7, 1., 1.);

  shape.dist = vine;
  shape.color = color;

  return shape;
}


Shape map(vec3 c){
  Shape vines = vines(c);
  return vines;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 v = (fragCoord.xy-0.5 * iResolution.xy) / iResolution.y;


  vec3 cam = vec3(0., 0., -10.); //Trig functions make the movements more realistic
  vec3 f = normalize(vec3(v, 1.));
  vec3 scene = cam;
  fragColor = vec4(0.);

  // Ray Marcher
  for(float z = 0.1 ; z <= 1.; z += 0.03){
    Shape c = map(scene); // Calc SDF
    if(c.dist < 0.01){
      fragColor = c.color*(1.-z); // Hit  - invert pixels
      break;
    }
    scene += f * c.dist;


  }
}
