#define PI 3.1415926535897932384626433833
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 m = iMouse.xy / iResolution.xy;
    uv.x += 4.* m.x;
    //uv.x *= 24.;
    float s = 0.;
    float alpha = (1. + 2.*m.y);
    //float alpha = iTime / 20.;
    if (iMouse.z < 1.)
        alpha = 2.;
    for (int i = 1; i < 101; ++i) {
        s += sin(PI * uv.x * pow(float(i), alpha)) / (PI * pow(float(i), alpha));
    }
    s *= 4.;
    fragColor = vec4(.004/abs(uv.y*4.-2. - s));

	fragColor += vec4(.01/(smoothstep(.0, .05, abs(uv.y-.5))), 0, 0, 1);
    fragColor += vec4(.003/abs(sin(PI * uv.x)));
}
