uniform float Time;
uniform vec2 mouse;
uniform vec2 resolution;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 position = ( fragCoord.xy / iResolution.xy ) / 2.0;

	float duration = sin(iTime/2.)*20.;

	vec3 color;
	color.g += sin(position.x*position.y*10000. * duration);

	color.g += sin(position.x*position.y*10011. * duration*0.5) * 0.6;

	color.g += sin(position.x*position.y*10022. * duration*0.25) * 0.4;

    // Output to screen
    fragColor = vec4(color,1.0);
}
