vec2 R;
vec4 T ( vec2 U ) {return texture(iChannel0,U/R);}
void mainImage( out vec4 C, in vec2 U )
{
    R = iResolution.xy;
    vec4 
        a = T(U+vec2(1,0)),
        b = T(U-vec2(1,0)),
        c = T(U+vec2(0,1)),
        d = T(U-vec2(0,1));

    vec4 g = vec4(a.zw-b.zw,c.zw-d.zw);
    vec2 dz = g.xz;
    vec2 dw = g.yw;
   	vec4 v = T(U);
    C.xyz = max(vec3(0),sin(1.5+5.*v.z+3.*(v.w)*v.w*vec3(1,2,3)));
    vec3 n = normalize(vec3(dz,.05));
    vec4 tx = texture(iChannel1,reflect(vec3(0,0,1),n));
    float p = 1e6*dot(dz,dz);
    p = atan(p*p)*.63661977237;
    float w = atan(3.*v.w)*.63661977237;
    C *= (0.7+0.3*tx);
}

vec2 R;
vec4 T ( vec2 U ) {return texture(iChannel0,U/R);}
void mainImage( out vec4 Q, in vec2 U )
{   R = iResolution.xy;

 	vec2 O = U,A = U+vec2(1,0),B = U+vec2(0,1),C = U+vec2(-1,0),D = U+vec2(0,-1);
 	vec4 u,a,b,c,d;
 	float p = 0.;
 	vec2 g = vec2(0);
 	#define I 2
 	for (int i = 0; i < I; i++) {
        u = T(U); U -= u.xy;
 		a = T(A); b = T(B); c = T(C); d = T(D);
        A -=a.xy; B -=b.xy; C -=c.xy; D -=d.xy; 
        p += length(U-A)+length(U-B)+length(U-C)+length(U-D)-4.;
        g += vec2(a.z-c.z,b.z-d.z);
 	}
 	u = T(U); a = T(A); b = T(B); c = T(C); d = T(D);
 	Q = T(U);
 	vec4 N = 0.25*(a+b+c+d);
 	Q = mix(Q,N, vec4(0,0,1,0));
 	Q.xy -= g/8./float(I);
 	Q.z += p/8.;
 	Q.z *= 0.9999;
 	if (iFrame < 1) Q = vec4(0,0,0,0);
 	if (length(U-vec2(0.925,0.5)*R) < 2.) Q.xyw = vec3(Q.xy*0.5+0.5*vec2(-0.2,0),1.);
 	if (U.x<1.||U.y<1.||R.x-U.x<1.||R.y-U.y<1.) Q.xy*=0.;
}

