void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = 2.4*fragCoord.xy/iResolution.y-1.2;

	vec2 z = p;
	float f = 1.;
	float g = 1.;
	float t = .7+.2*sin(iTime);
	for( int i=0; i<128; i++ ) 
	{
		z = vec2( z.x*z.x-z.y*z.y, 2.0*z.x*z.y ) - p;		
		f = min( f, abs(dot(z-p,z-p) ));
		g = min( g, abs(dot(z,z) ));
		p*=t/dot(p,p) ;p.y=-p.y;

	}

	f=1.+log(f)/6.0;

	fragColor = 1.-clamp((1.+log(g)/12.0)*vec4(g,f,f*f,1.0),0.,1.);
}
