#define tau 6.2831853071795864769
#define heart vec4(1.0,0.0,0.0,1.0)
#define bright 0.5
//bright radius best: 1000,2000,3000,5000,100
#define brightRadius 2000
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 u = fragCoord.xy-iResolution.xy/2.;
    u.y-=abs(u.x)*sqrt((100.-abs(u.x))/66.);
   	float c = u.x*u.x+u.y*u.y;
    float r = (0.5*sin(tau*iTime+u.y/60.)+0.5)*0.4;
    r*=r*r*r;
    r+=0.2;
    r*=r*99999.;
    fragColor = smoothstep(r+300., r,c)*heart+(1.-smoothstep(0.,r+float(brightRadius),c))*bright;
}
