vec2 distort(vec2 r, float alpha)
{
    //float a = max(0.0, (.5 - length(r))) * alpha; //linear
   // float a = smoothstep(0.0, 1.0, (1.0 - length(r))) * alpha; //smooth
    float a = smoothstep(0.0, 1.0, max(0.0, (.5 - length(r)))) * alpha; //smooth
   float s = sin(a);
	float c = cos(a);
	mat2 m = mat2(c, -s, s, c);
	return m * r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv.x *= (1920.0/1080.0);
    vec2 center = vec2(.5 * 1920.0/1080.0, .5);
    vec2 v = (uv - center);
    vec4 m = iMouse / iResolution.xxxx;

    float amount = m.x * 10.0 - 5.0;
    //float amount = (sin(iTime * 4.0) * 1.0 ) ;

    uv = center + distort(v, amount);
	fragColor = texture(iChannel0, uv);
}
