void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if (iMouse.w > 0. && iMouse.x > fragCoord.x) {
        // this is just to allow the visualisation of the input
        fragColor = texelFetch(iChannel1, ivec2(fragCoord.xy / 4.), 0);
        return;
    }

    ivec2 texPos = ivec2(fragCoord.xy / 2.);
    int id = int(floor(mod(fragCoord.x, 2.)) + floor(mod(fragCoord.y, 2.)) * 2.); // 0-1 / 2-3

    vec4 B = texelFetch(iChannel0, texPos + ivec2(0,-1), 0);
    vec4 D = texelFetch(iChannel0, texPos + ivec2(-1,0), 0);
    vec4 E = texelFetch(iChannel0, texPos, 0);
    vec4 F = texelFetch(iChannel0, texPos + ivec2(1,0), 0);
    vec4 H = texelFetch(iChannel0, texPos + ivec2(0,1), 0);

    vec4 result;

    if( (B != H) && (D != F) ) {
        if (id == 0) {
            result = (D == B) ? D : E;
        } else if (id == 1) {
            result = (B == F) ? F : E;
        } else if (id == 2) {
            result = (D == H) ? D : E;
        } else {
            result = (H == F) ? F : E;
        }
    } else {
        result = E;
    }

    fragColor = result;

    if (iMouse.w > 0. && abs(iMouse.x - fragCoord.x) < 2.) {
        fragColor.rgb = mix(fragColor.rgb, vec3(1.,1.,0.33), 0.66);
        return;
    }
}

// This filter only works on pixellized images, it was specifically implemented to enhance the output of console emulation
// The original filter code by Andrea Mazzoleni is GPL-2, so consider this GPL-2 too, I guess ?
// This is specifically based on https://github.com/marcoc2/dpixel/blob/master/Filters/Scale2xFilter.cpp

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // take the input, reduce it to a 4th of the screen size and quantize it to 4 shades of grey
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv *= 4.;
    vec4 color = uv.x < 1. && uv.y < 1. && uv.y > 0. ? texture(iChannel0, uv) : vec4(0., 0., 0., 1.);
    color = vec4(vec3(floor(pow((color.r + color.g + color.b + 0.1) / 3., 0.75) * 3.) / 3.), 1.);
    fragColor = color;
}

