vec3 backgroundColor = vec3(0.9, 0.99, 0.99);
float sphereSize = 0.7;
float sphereShininess = 0.7;
vec3 sphereDiffuse = vec3(0.2, 0.4, 0.5);
vec3 sphereSpecular = vec3(1.0, 1.0, 1.0);
vec2 specularPoint = vec2(0.0, 0.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 p = uv * 2.0 - 1.0;
	p.x *= iResolution.x / iResolution.y;

	// Update position of the sphere
	// p.x += sin(iTime) * 0.25;
	// p.y += sin(iTime) * 0.25;

	// Update position of the specular point.
	specularPoint.x += sin(iTime * 1.7) * 0.15;
	specularPoint.y += cos(iTime * 1.3) * 0.15;

	float r = sqrt(dot(p, p));

	vec3 color = backgroundColor;

	// Add Shadow
	float f = smoothstep(sphereSize * 0.9, sphereSize, length(p + specularPoint));
	color -= mix(color, vec3(0.0), f) * 0.2;

	if (r < sphereSize)
	{
		vec3 background = color;

		// Set diffuse color
		color = sphereDiffuse;

		// Add dark borders
		f = smoothstep(sphereSize * 0.7, sphereSize, r);
		color = mix(color, sphereDiffuse * 0.45, f);

		// Add Specular 
		f = 1.0 - smoothstep(-0.2, 0.6, length(p - specularPoint));
		color += f * sphereShininess * sphereSpecular;

		// Anti-aliasing
		f = smoothstep(sphereSize - 0.02, sphereSize, r);
		color = mix(color, background, f);
	}

	fragColor = vec4(color, 1);
}
